public class Bean extends ToppingDecorator
{
	Bean(Food food){
		this.myToppings = food.myToppings;
		myStr = "Bean";
		addTopping();
	}
	
	public void addTopping()
	{
		myToppings.add(myStr);
		price += 500;
	}
}
