public class JalapenoPepper extends ToppingDecorator
{
	public JalapenoPepper(Food food){
		this.myToppings = food.myToppings;
		this.price = food.price;
		myStr = "JalapenoPepper";
		addTopping();
	}
	
	public void addTopping()
	{
		myToppings.add(myStr);
		price += 500;
	}
}
