public class WedgePotato extends ToppingDecorator
{
	WedgePotato(Food food){
		this.myToppings = food.myToppings;
		myStr = "WedgePotato";
		addTopping();
	}
	
	public void addTopping()
	{
		myToppings.add(myStr);
		price += 500;
	}
}
